SELECT	Courses.CourseName,CourseRegistrations.Grade FROM Students, Degrees, StudentRegistrationsToDegrees,CourseRegistrations,CourseOffers,Courses WHERE Students.StudentID = 1 AND Degrees.DegreeId = 2524 AND StudentRegistrationsToDegrees.studentID = students.studentID AND	StudentRegistrationsToDegrees.DegreeId = Degrees.DegreeId AND CourseRegistrations.StudentRegistrationId = StudentRegistrationsToDegrees.StudentRegistrationId AND CourseRegistrations.CourseOfferId = CourseOffers.CourseOfferId AND Courses.CourseId = CourseOffers.CourseId AND CourseRegistrations.Grade > 5 ORDER BY CourseOffers.year, CourseOffers.quartile;