SELECT * FROM (SELECT good_student.sid, count(good_student.good_grades) as good_times FROM good_student GROUP BY good_student.sid) AS result WHERE result.good_times >=%1% ORDER BY sid;
SELECT StudentRegistrationsToDegrees.DegreeId,active.BirthyearStudent,active.gender, GPA.gpa_score AS avgGrade FROM StudentRegistrationsToDegrees, active, GPA WHERE StudentRegistrationsToDegrees.studentregistrationid = GPA.srid AND StudentRegistrationsToDegrees.studentid = active.studentid GROUP BY CUBE (StudentRegistrationsToDegrees.DegreeId,active.BirthyearStudent,active.gender, GPA.gpa_score) ORDER BY StudentRegistrationsToDegrees.DegreeId,active.BirthyearStudent,active.gender;
SELECT C.CourseName, CO.Year, CO.Quartile FROM Courses as C, CourseOffers as CO, (SELECT S.courseOfferID FROM students_per_courseoffer as S, assistants_per_courseOffer as A WHERE S.courseOfferID = A.courseOfferID AND S.students/A.assistants > 50) as L WHERE L.CourseOfferId = CO.CourseOfferId and C.CourseID = CO.CourseID ORDER BY CO.Courseofferid;

