SELECT courseoffers_one.CourseOfferId,courseoffers_one.CourseId,courseoffers_one.Year,courseoffers_one.Quartile, courses.CourseName,courses.CourseDescription,courses.DegreeId,courses.ECTS, degrees.Dept,degrees.DegreeDescription,degrees.TotalECTS, teachers.TeacherId,teachers.TeacherName,teachers.Address,teachers.BirthyearTeacher,teachers.Gender FROM (SELECT * FROM courseoffers WHERE courseOffers.courseofferid = 1) AS courseoffers_one,courses,degrees,teachers,teacherAssignmentsToCourses WHERE courseoffers_one.CourseId = courses.CourseId AND courses.DegreeId = degrees.DegreeId AND teacherAssignmentsToCourses.TeacherId = teachers.TeacherId AND teacherAssignmentsToCourses.CourseOfferId  = courseoffers_one.CourseOfferId;
SELECT courseoffers.courseofferid,courseoffers.courseid, courseoffers.year, courseoffers.quartile, students.studentid,students.StudentName,students.address,students.BirthyearStudent,students.Gender, degrees.degreeid, degrees.dept, degrees.degreedescription, degrees.totalects FROM courseoffers, degrees, students,StudentRegistrationsToDegrees,StudentAssistants WHERE StudentRegistrationsToDegrees.studentregistrationid=3 and StudentRegistrationsToDegrees.StudentId = students.StudentId AND StudentRegistrationsToDegrees.DegreeId = degrees.DegreeId AND StudentAssistants.StudentRegistrationId = StudentRegistrationsToDegrees.studentregistrationid and courseoffers.courseofferid = StudentAssistants.courseofferid; 
SELECT AVG(Grade) FROM (SELECT * FROM Courseregistrations_failed UNION ALL SELECT * FROM Courseregistrations_4 UNION ALL SELECT * FROM Courseregistrations_passed) AS Cr_all WHERE studentregistrationid=3;
